import React, { Component } from 'react';
import './App.css';
import ToDo from './components/ToDo.js';

class App extends Component {
  constructor(props) {
   super(props);
   this.state = {
  todos: [
    { description: 'Walk the cat', isCompleted: true, isDeleted: false },
    { description: 'Throw the dishes away', isCompleted: false, isDeleted: false },
    { description: 'Buy new dishes', isCompleted: false, isDeleted: false }
  ]
};
 }
 toggleComplete(index) {
    const todos = this.state.todos.slice();
    const todo = todos[index];
    todo.isCompleted = todo.isCompleted ? false : true;
    this.setState({ todos: todos });
  }
deleteTodo(index) {
     const todels = this.state.todos.filter();
     const todel = todels[index];
     todel.isDeleted = todel.isDeleted ? false : true;
     this.setState({ todels: todels });
   }


  render() {
    return (
      <div className="App">
       <ul>
          { this.state.todos.map( (todo, index) =>
          <ToDo key={ index } description={ todo.description } isCompleted={ todo.isCompleted } toggleComplete={ () => this.toggleComplete(index) } />
)}
       </ul>
      </div>
    );
  }
}

export default App;
